let burgerMenu=document.querySelector(".menu__icon"),linkMenu=document.querySelectorAll(".menu__link");function burger(){burgerMenu.classList.toggle("_active"),document.body.classList.toggle("_lock"),burgerMenu.classList.contains("_active")?(burgerMenu.classList.add("_changeColor"),linkMenu.forEach((e=>{e.classList.add("_changeColor")}))):(burgerMenu.classList.remove("_changeColor"),linkMenu.forEach((e=>{e.classList.remove("_changeColor")}))),document.querySelector(".menu__body").classList.toggle("_active")}function scrollFunction(){document.body.scrollTop>80||document.documentElement.scrollTop>80?document.querySelector(".header").classList.add("_active"):document.querySelector(".header").classList.remove("_active")}burgerMenu.addEventListener("click",burger),window.onscroll=function(){scrollFunction()};